<?php


namespace Cheezykins\AdventOfCode\Test\Answer\DayFive;


use Cheezykins\AdventOfCode\DayFive\Polymer;
use PHPUnit\Framework\TestCase;

class DayFiveAnswersTest extends TestCase
{
    public function testPartOneAnswer()
    {

        $polymerString = trim(file_get_contents(__DIR__ . '/fixtures/polymer.txt'));

        $p = new Polymer($polymerString);
        $size = $p->activate();

        $this->assertEquals(11118, $size);
    }

    public function testPartTwoAnswer()
    {
        $polymerString = '';

        $p = new Polymer($polymerString);

        $size = $p->optimize();
        $this->assertEquals(6948, $size);

    }
}
